
public class 연산자문제_8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	    int num1=10, num2=10;
	    System.out.printf("%d \n", ++num1); //출력 전에 증가 ==> 출력 // 11
	    System.out.printf("%d \n", num1);  // 11
	    System.out.printf("%d \n", num2++);  // 출력 후에 증가 ==> 10
	    System.out.printf("%d \n", num2);  // 후에 증가했으니 11

	}

}
