
public class 제어문_반복문_6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 1. A~Z까지 출력
		for(char c='A';c<='Z';c++)
		{
			System.out.print(c+" ");
		}
		System.out.println(); // 다음 줄에 출력하는 기능 
		for(char d='a';d<='z';d++)
		{
			System.out.print(d+" ");
		}
	}

}
